    private void printBillBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_printBillBtnActionPerformed
     txtResult.setText("");

    int meterCode;
    try {
        meterCode = Integer.parseInt(txtMeterCode.getText().trim());
    } catch (NumberFormatException e) {
        txtResult.setText("Invalid meter code.");
        return;
    }

    int customerId = Operator.getCustomerIdByMeterCode(meterCode);

    if (customerId == -1) {
        txtResult.setText("Meter code not linked to any customer.");
        return;
    }

    String file = "Files\\Bills.txt";
    ArrayList<String> bills = FileHandler.read(file);

    for (String line : bills) {

        if (line.startsWith("BillID")) continue;

        String[] parts = line.split(",");

        int fileCustomerId = Integer.parseInt(parts[1]);

        if (fileCustomerId == customerId) {
            txtResult.setText(
                "Bill ID : " + parts[0] + "\n" +
                "Customer ID : " + parts[1] + "\n" +
                "Amount : " + parts[2] + "\n" +
                "Date : " + parts[3] + "\n" +
                "Status : " + parts[4]
            );
            return;
        }
    }

    txtResult.setText("No bill found for this customer.");
    }//GEN-LAST:event_printBillBtnActionPerformed

    private void defineTariffBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_defineTariffBtnActionPerformed
        try {
            String region = regionField.getText();
            double price = Double.parseDouble(priceField.getText());
            
            if (region.isEmpty()) {
                JOptionPane.showMessageDialog(this, "Please enter a region!", "Error", JOptionPane.ERROR_MESSAGE);
                return;
            }
            
            Operator.defineTariff(region, price);
            
            JOptionPane.showMessageDialog(this, "Tariff defined successfully!");
            regionField.setText("");
            priceField.setText("");
        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(this, "Please enter a valid price!", "Error", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_defineTariffBtnActionPerformed

    private void viewBillsBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_viewBillsBtnActionPerformed
        String region = regionField2.getText();
        
        if (region.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Please enter a region!", "Error", JOptionPane.ERROR_MESSAGE);
            return;
        }
        
        ArrayList<String> bills = Operator.viewBillsByRegion(region);
        
        if (bills.isEmpty()) {
            billsOutputArea.setText("No bills found for region: " + region);
        } else {
            StringBuilder sb = new StringBuilder();
            for (String bill : bills) {
                sb.append(bill).append("\n");
            }
            billsOutputArea.setText(sb.toString());
        }
    }//GEN-LAST:event_viewBillsBtnActionPerformed

