    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here
        String read = reading.getText().trim();
        String code = MeterCode.getText().trim();
        if(read.isEmpty() || code.isEmpty()){
            JOptionPane.showMessageDialog(this ,"Please fill in all fields" , "ERROR"  ,  JOptionPane.ERROR_MESSAGE);
            return;// TODO add your handling code here:
        }
        if( code.matches("\\d+")) {
        System.out.println("Meter code is: " + code);
        } else {
        JOptionPane.showMessageDialog(null, "Please enter numbers only for Meter Code!" , "ERROR" ,JOptionPane.ERROR_MESSAGE);
        MeterCode.setText("");
        return;
        }
        if( read.matches("\\d+")) {
        System.out.println("Monthly reading is: " + read);
        } else {
        JOptionPane.showMessageDialog(null, "Please enter numbers only for Monthly Reading!" , "ERROR" ,JOptionPane.ERROR_MESSAGE);
        reading.setText("");
        return;
        }  
 txtResultArea.setText("");

    int meterCode;

    try {
        meterCode = Integer.parseInt(MeterCode.getText().trim());
    } catch (NumberFormatException e) {
        txtResultArea.setText("Please enter a valid meter code.");
        return;
    }

    String file = "Files\\MeterReadings.txt";
    ArrayList<String> meterReadings = FileHandler.read(file);

    int lastOldReading = -1;
    int currentReading = -1;

    for (String line : meterReadings) {

        // Skip header
        if (line.startsWith("ReadingID")) continue;

        String[] parts = line.split(",");

        int fileMeterCode = Integer.parseInt(parts[2].trim());
        int oldReadingValue = Integer.parseInt(parts[3].trim());
        int newReadingValue = Integer.parseInt(parts[4].trim());

        // get last stored readings for this meter
        if (fileMeterCode == meterCode) {
            lastOldReading = oldReadingValue;
            currentReading = newReadingValue;
        }
    }

    if (lastOldReading == -1 || currentReading == -1) {
        txtResultArea.setText("Meter code not found.");
        return;
    }

    if (Integer.parseInt(read) >= lastOldReading) {
        txtResultArea.setText(
            "Reading is VALID\n" +
            "Old Reading: " + lastOldReading + "\n" +
            "New Reading: " + read
        );
    } else {
        txtResultArea.setText(
            "Reading is INVALID\n" +
            "Old Reading: " + lastOldReading + "\n" +
            "New Reading: " + read
        );
    }        
    }//GEN-LAST:event_jButton1ActionPerformed

